INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Actinidiaceae',NULL,NULL,'Kiwi');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Alliaceae',4,'Insecticide et fongicide','Ail, ciboulette, oignon, poireau '||x'0a0a'||'Sensibles à la pourriture blanche. '||x'0a0a'||'Plantes herbacées vivaces, le plus souvent à bulbe, rarement rhizomateuses. Feuilles simples, fleurs régulières. Le fruit est une capsule. Les graines sont albuminées. '||x'0a0a'||'Régions tempérées chaudes et subtropicales. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Amaranthaceae',3,NULL,'Amarante, betterave, chénopodes, épinard, poirée (ou bette) '||x'0a0a'||'Maladies racinaires comme le rhizoctone. '||x'0a0a'||'Plantes herbacées.');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Apiaceae',4,'Allélopathique fort, répulsif, hôte d''insectes auxiliaires.','Carotte, Céleri, Coriandre, Cumin, Fenouil, Panais, Persil '||x'0a0a'||'Risques de maladies fongiques du sol. '||x'0a0a'||'Tige creuse et dotée de canaux sécréteurs de résines et d''essences odorantes. Feuilles alternes, composées, pennées, au pétiole engainant au niveau des nœuds. Leur inflorescence est généralement une ombelle. Fruits secs doubles. '||x'0a0a'||'Régions tempérées. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Asparagaceae',4,NULL,'Asperge');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Asteraceae',3,'Allélopathique fort, hôte d''insectes auxiliaires.','Artichaut, chicorée, laitue, tournesol '||x'0a0a'||'Risques de sclérotiniose. '||x'0a0a'||'Tige herbacée généralement dressée. Feuilles sans stipules, souvent simples. Fleurs minuscules, réunies en inflorescences appelées capitules. '||x'0a0a'||'Principalement dans les régions tempérées. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Boraginaceae',4,NULL,'Buglosse, bourrache, myosotis, vipérine'||x'0a0a'||'Plantes dicotylédones à feuilles très velues, productrice d''huiles essentielles.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Brassicaceae',3,'Insecticide et répulsif.','Choux, colza, cresson, moutarde, navet, raifort '||x'0a0a'||'Risques d’hernie du chou, altises. '||x'0a0a'||'Plantes herbacées. Iinflorescence généralement en grappe simple. '||x'0a0a'||'Surtout présentes dans l''hémisphère nord. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Cannabaceae',NULL,NULL,'Cannabis, houblon');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Convolvulaceae',4,NULL,'Liserons, patate douce '||x'0a0a'||'Sensible aux maladies fongiques (fusariose, pourriture noire) et aux nématodes à galles (Meloidogyne spp.). '||x'0a0a'||'Racine pivotante, ramifiée, charnue. Tiges dressées ou prostrées, cylindriques, ramifiées. Feuilles alternes, pétiolées, sans stipules. Exsudent souvent un latex blanc. Fleurs généralement grandes et très colorées. '||x'0a0a'||'Régions tropicales et subtropicales '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Couverts végétaux',0,NULL,NULL);
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Cucurbitaceae',3,NULL,'Concombre, courges, courgettes, melon '||x'0a0a'||'Problèmes de fusariose et d’oïdium. '||x'0a0a'||'Généralement des plantes herbacées, à port rampant ou grimpant. Feuilles alternes sans stipule. Grandes fleurs. Le fruit est en général une baie. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Ebenaceae',NULL,NULL,'Plaqueminier, ébène.'||x'0a0a'||'Arbres et arbustes.'||x'0a0a'||'Principalement des régions tropicales.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Euphorbiaceae',5,NULL,'Manioc, ricin '||x'0a0a'||'Sensible à la bactériose vasculaire, risque de  nématodes et de cochenilles farineuses. '||x'0a0a'||'Plantes à fleurs dicotylédones. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Fabaceae',3,'Apport azote, hôte d''insectes auxiliaires.','Arachide, fèves, haricots, lentilles, luzerne, pois chiche, soja, trèfle '||x'0a0a'||'Sensibles aux nématodes. '||x'0a0a'||'Activité symbiotique de fixation de l''azote atmosphérique grâce à des bactéries. '||x'0a0a'||'Wikipédia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Grossulariaceae',NULL,NULL,'Groseilliers, cassissier'||x'0a0a'||'Arbustes dicotylédones.'||x'0a0a'||'Zones froides à subtropicales.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Hydrophyllaceae',3,NULL,'Phacélie');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Juglandaceae',NULL,NULL,'Noyer, pacanier');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Lamiaceae',3,'Allélopathique fort, insecticide, hôte d''insectes auxiliaires.','Basilic, mélisse, menthe, romarin, sauge, serpolet, thym '||x'0a0a'||'Sensibles à  la fusariose, l’oïdium et la verticilliose. '||x'0a0a'||'Producteurs d''huiles essentielles, souvent mellifères. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Lythraceae',NULL,NULL,'Grenadier, salicaire, châtaigne d''eau'||x'0a0a'||'Arbres ou herbes vivaces ou annuelles, à fleurs dicotylédones, certaines aquatiques. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Malvaceae',NULL,NULL,'Hibiscus, tilleul, rose trémière, guimauve, baobab.'||x'0a0a'||'Principalement régions tropicales d''Amérique du Sud, minoritairement régions tempérées (Méditerranée).'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Moraceae',NULL,NULL,'Figuier, Mûrier platane ...');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Myrtaceae',NULL,NULL,'Eucalyptus, Feijoa, giroflier'||x'0a0a'||'Arbres ou arbustes, à fleurs dicotylédones, souvent des producteurs d''huiles aromatiques.'||x'0a0a'||'Zones tempérées, subtropicales à tropicales.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Oleaceae',NULL,NULL,'Lilas, jasmin, forsythia, frêne, troène, olivier '||x'0a0a'||'Plantes dicotylédones à fleurs tétramères.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Poaceae',4,'Engrais et hôte d''insectes auxiliaires.','Blé, maïs, riz '||x'0a0a'||'Plantes herbacées à tiges cylindriques aux entrenœuds creux. Feuilles alternes dont la gaine enveloppe la tige. Inflorescence élémentaire en épillets, fleurs réduites aux organes sexuels. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Polygonaceae',4,NULL,'Oseille, rhubarbe, sarrasin'||x'0a0a'||'Sensible à certaines rouilles et fusarioses.'||x'0a0a'||'Plantes herbacées, le plus souvent vivaces. Racines fibreuses qui  forment des racines secondaires latérales qui colonisent l''espace disponible. Feuilles simples généralement alternes.  Riches en acide oxalique (toxiques en grande quantité).'||x'0a0a'||'Régions de l''hémisphère nord.'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Rosaceae',NULL,NULL,'Abricotier, amandier, aubépine, cerisier, cognacier, cynorrhodon, fraisier, framboisier, mûrier, nèflier, pêcher, plaquebière, poirier, pommier, potentille, prunier, prunelier, ronce');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Rutaceae',NULL,NULL,'Agrumes'||x'0a0a'||'Plantes productrices d''huiles essentielles.'||x'0a0a'||'Régions tempérées à tropicales'||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Solanaceae',4,'Apauvri le sol','Aubergine, piment, pomme de terre, tomate '||x'0a0a'||'Feuilles généralement alternes. 5 pétales soudés entre eux à des hauteurs variables. Fruit charnu ou sec. Graines habituellement oléagineuses. '||x'0a0a'||'Wikipedia');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Tropaeolaceae',4,NULL,'Capucine'||x'0a0a'||'Plantes dicotylédones, herbacées annuelles ou pérennes.');
INSERT INTO Familles (Famille,Intervalle,Effet,Notes) VALUES ('Valerianaceae',3,NULL,'Mâche, valériane '||x'0a0a'||'Sensible au mildiou (Peronospora valerianellae), à la sclérotiniose. Rique de de fonte des semis (Pythium, Rhizoctonia).');
